import React from "react";
import "./App.css";

import List from "./componets/List";

const App = () => {
  state = {
    users: [
      ["foo", "bar"],
      ["foo", "bar"],
      ["foo", "bar"],
      ["foo", "bar"],
    ],
  };

  const [users, setUsers] = useState([
    ["foo", "bar"],
    ["foo", "bar"],
    ["foo", "bar"],
    ["foo", "bar"],
  ]);

  const moveToNext = (index, usersIndex) => {
    const users = this.state.users;
    users[usersIndex + 1].push(users[usersIndex][index]);
    users[usersIndex].splice(index, 1);
    this.setState({
      users,
    });
  };

  const moveToPrev = (index, usersIndex) => {
    const users = this.state.users;
    users[usersIndex - 1].push(users[usersIndex][index]);
    users[usersIndex].splice(index, 1);
    this.setState({
      users,
    });
  };

  const addItem = (usersIndex) => {
    const users = this.state.users;
    const add = prompt("", "");
    if (add === null) {
      return;
    }
    users[usersIndex].push(add);
    this.setState({
      users,
    });
  };

  return (
    <div className="App">
      <div className="container">
        <List
          blocktext={this.state.users}
          moveToNext={this.moveToNext}
          moveToPrev={this.moveToPrev}
          addItem={this.addItem}
        />
      </div>
    </div>
  );
};

export default App;
