import React, { useState } from 'react';
import './App.css';
import text from './constants/text';

import List from './componets/List';

const App = () => {
  const [users, setUsers] = useState([...text]);

  const moveToNext = (index, usersIndex) => {
    users[usersIndex + 1].push(users[usersIndex][index]);
    users[usersIndex].splice(index, 1);
    setUsers([...users]);
  };

  const moveToPrev = (index, usersIndex) => {
    users[usersIndex - 1].push(users[usersIndex][index]);
    users[usersIndex].splice(index, 1);
    setUsers([...users]);
  };

  const addItem = (usersIndex) => {
    const add = prompt('', '');
    if (add === null) {
      return;
    }
    users[usersIndex].push(add);
    setUsers([...users]);
  };

  return (
    <div className='App'>
      <div className='container'>
        <List
          users={users}
          moveToNext={moveToNext}
          moveToPrev={moveToPrev}
          addItem={addItem}
        />
      </div>
    </div>
  );
};

export default App;
