import React, { useState, useEffect } from "react";
import "./App.css";

import Item from "./componets/List/Items";

const usersNames = ["Winnie", "Bob", "Thomas", "George"];

const App = () => {
  const [users, setUsers] = useState([
    ["foo", "bar"],
    ["foo", "bar"],
    ["foo", "bar"],
    ["foo", "bar"],
  ]);

  const moveToNext = (index, usersIndex) => {
    const users0 = users;
    users0[usersIndex + 1] = users0[usersIndex][index];
    users0[usersIndex].splice(index, 1);
    setUsers(users0);
  };

  const moveToPrev = (index, usersIndex) => {
    const users0 = users;
    users0[usersIndex - 1] = users0[usersIndex][index];
    users0[usersIndex].splice(index, 1);
    setUsers(users0);
  };

  const addItem = (usersIndex) => {
    const users = this.state.users;
    const add = prompt("", "");
    if (add === null) {
      return;
    }
    users[usersIndex].push(add);
    this.setState({
      users,
    });
  };

  useEffect(() => {
    console.log("hi users changed");
  }, [users]);

  return (
    <div className="App">
      <div className="container">
        {users.map((v, i, arr) => (
          <div>
            <h3
              className={
                (i === 0 && "user1 users") ||
                (i === 1 && "user2 users") ||
                (i === 2 && "user3 users") ||
                (i === 3 && "user4 users")
              }
            >
              {usersNames[i]}
            </h3>
            {arr[i].map((v, i2, arr) => (
              <Item
                text={arr[i2]}
                index={i2}
                usersIndex={i}
                moveToNext={moveToNext}
                moveToPrev={moveToPrev}
              />
            ))}
            <button className="end" onClick={() => addItem(i)}>
              + Add a card
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
